import React from 'react';

// Helper to align output always as a matrix/grid.
// Determinant is a scalar—showed inside a single cell with label.
const OutputPanel = ({ output, steps, error }) => {
  let isMatrix = Array.isArray(output);
  let matrixContent = null;

  if (isMatrix && output.length > 0 && Array.isArray(output[0])) {
    // Typical matrix (2D array)
    matrixContent = (
      <div className="matrix-output">
        {output.map((row, i) => (
          <div key={i} className="matrix-row">
            {row.map((val, j) => (
              <span key={j} className="cell">{val}</span>
            ))}
          </div>
        ))}
      </div>
    );
  } else if (!isMatrix && output !== '' && output !== undefined && output !== null) {
    // Scalar; show as a 1x1 matrix
    matrixContent = (
      <div className="matrix-output">
        <div className="matrix-row">
          <span className="cell">{output}</span>
        </div>
      </div>
    );
  }

  return (
    <div className="output-panel">
      <h2>Output</h2>
      {error && <div className="error">{error}</div>}
      {matrixContent}
      {steps && <div className="steps"><strong>Steps:</strong> {steps}</div>}
    </div>
  );
};

export default OutputPanel;
